import React, { useState } from 'react'



const Forms = () => {
    const [name,setName]  = useState('Jatin')
    const [email,setEmail]  = useState('jatinsharma2231@gmail.com')
    const [phone,setPhone]  = useState('9050603853')

    const changeHandler = (e) =>{
        let inputName = e.targe.name;
        let inputValue = e.targe.value;
        if(inputName === "name"){
            setName(inputValue)
        }else if(inputName === "email"){
            setEmail(inputValue);
        }else if(inputName === "phone"){
            setPhone(inputValue);
        }
    }
    
    const formSubmitHandler = (e) =>{
        e.preventDefault();
        // let data = {
        //     name : name,
        //     email:email,
        //     phone:phone,
        // }
        // if in obj key and value are same you can write like this 
        let data = {name,email,phone,}
        console.log('data',data)
    }
    return (
    <form action='#' >
        <div>User Validation</div>
        <div>
            <label >Name</label>
            <input type="text" placeholder="Enter your Name" value={name} onChange={changeHandler} />
        </div>
        <div>
            <label >Email</label>
            <input type="email" placeholder="Enter your email" value={email} onChange={changeHandler} />
        </div>
        <div>
            <label >Phone</label>
            <input type="number" placeholder="Enter your Phone" value={phone} onChange={changeHandler} />
        </div>
        <input type="submit" value="save" />
    </form>
  )
}

export default Forms
