import React, { useState } from "react";
import { data } from "../../data/data";
import Card from "./Card";

const Cards = () => {
  const [filteredFoods, setFilteredFoods] = useState(data);
  // const [selectedCategory, setSelectedCategory] = useState('all'); // Default category is "all"

  const filterProducts = (category) => {
    if (category === "all") {
      // If "all" is selected, show all products
      setFilteredFoods(data);
    } else {
      // Filter products by category
      const filtered = data.filter((product) => product.category === category);
      setFilteredFoods(filtered);
    }
    // setSelectedCategory(category);
  };
  const [searchTerm, setSearchTerm] = useState("");
  return (
    <div>
      <center><h1>Food App</h1></center>
    
      <div className="d-flex justify-content-around mt-4">
        <div>
          <button
            className="btn btn-danger m-2"
            onClick={() => filterProducts("all")}
          >
            All
          </button>
          <button
            className="btn btn-danger m-2"
            onClick={() => filterProducts("Dinner")}
          >
            Dinner
          </button>
          <button
            className="btn btn-danger m-2"
            onClick={() => filterProducts("Breakfast")}
          >
            Breakfast
          </button>
          <button
            className="btn btn-danger m-2"
            onClick={() => filterProducts("Lunch")}
          >
            Lunch
          </button>
        </div>

        <input
          style={{ width: "40%", height: "40px" }}
          type="text"
          placeholder="Serach here ..."
          onChange={(e) => {
            setSearchTerm(e.target.value);
          }}
        />
      </div>

      <div
        style={{
          display: "flex",
          flexWrap: "wrap",
          gap: 30,
          alignItems: "center",
          justifyContent: "center",
          marginTop: "30px",
        }}
      >
        {filteredFoods
          .filter((val) => {
            if (searchTerm === " ") {
              return val;
            } else if (
              val.title.toLowerCase().includes(searchTerm.toLowerCase())
            ) {
              return val;
            }
          })
          .map((items) => {
            return <Card data={items} />;
          })}
      </div>
    </div>
  );
};

export default Cards;
