const initialState ={
    cart:[]
}

const cartReducer = (state=initialState,action)=>{
    switch(action.type){
        case 'addToCart' : 
        return {
           ...state,
           cart:[...state,action.product]
        }
        
        default:
            return state
    }
}

export default cartReducer
