import React, { useState } from 'react'

const BgColorChange = () => {
    const [bgColor, setBgColor] = useState('gray')
    const [buttonColor, setButtonColor] = useState('white')
    const [buttonTextColor , setBunttonTextColor] =  useState('black')
    
    const buttonRedColor = () => {
        setButtonColor('red');
        setButtonTextColor('white')
        setBgColor('red')
    }
    const buttonYellowColor = () =>{
        setButtonColor('yellow');
        setButtonTextColor('white')
        setBgColor('yellow')
    }
    const buttonBlackColor = () =>{
        setButtonColor('black');
        setButtonTextColor('white')
        setBgColor('black')
    }
  return (
    <div style={{backgroundColor:bgColor,height:'100vh'}}
    >
        <button onClick={buttonRedColor} style={{backgroundColor:buttonColor,color:buttonTextColor}}>Red</button>
        <button onClick={buttonYellowColor} style={{backgroundColor:buttonColor,color:buttonTextColor}}>Yellow</button>
        <button onClick={buttonBlackColor} style={{backgroundColor:buttonColor,color:buttonTextColor}}>Black</button>
    </div>
  )
}

export default BgColorChange
